package my_pack_for_double;

public class Area {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double pi,r,a;
		
		r = 10.8;
		pi = 3.1416; //value of pi
		
		a = pi*r*r;//calculate area of circle using its formula
		
		System.out.println("Area of circle is calculated "+a);
		
	}

}
